'@ -split "`n"

    Write-Host
    Write-Host

    foreach ($line in $banner) {
        foreach ($char in $line.ToCharArray()) {
            Write-Host -NoNewline $char
            Start-Sleep -Milliseconds $Delay
        }
        Write-Host  # Move to next line
    }

    Write-Host
    Write-Host
}
